int solve(int a,int b){
	if(b==n)return 1;
	int ans=0;
	for(int i=0,j=v[a].size();i<j;i++){
		if(!vis[v[a][i]]){
			vis[v[a][i]]=1;
			ans+=solve(v[a][i],b+1);
			vis[v[a][i]]=0;
		}
	}
